    // Función para mostrar los artistas manualmente
    function displayManualArtists() {
        const artistContainer = document.querySelector('.artists'); // Contenedor de artistas
        if (!artistContainer) {
            console.error('El contenedor de artistas no se encontró en el DOM.');
            return;
        }
    
        artistContainer.innerHTML = ''; // Limpiar contenido previo
    
        manualArtists.forEach(artist => {
            const artistElement = document.createElement('a');
            artistElement.href = artist.link;
            artistElement.target = '_blank';
            artistElement.classList.add('artist');
    
            const img = document.createElement('img');
            img.src = artist.image;
            img.alt = artist.name;
            img.title = artist.name;
    
            // Depuración adicional
            img.onload = () => {
                console.log(`Imagen cargada correctamente para: ${artist.name}`);
            };
    
            img.onerror = () => {
                console.error(`Error al cargar la imagen del artista: ${artist.name}, URL: ${artist.image}`);
                img.src = 'https://via.placeholder.com/200?text=No+Image'; // Imagen de respaldo
            };
    
            const name = document.createElement('p');
            name.textContent = artist.name;
    
            artistElement.appendChild(img);
            artistElement.appendChild(name);
    
            artistContainer.appendChild(artistElement);
        });
    
        console.log('Artistas manuales cargados correctamente.');
    }
    
    // Llama a la función para cargar los artistas manuales
    displayManualArtists();
    
    function initializeSlider() {
        const artistContainer = document.querySelector('.artists');
        if (!artistContainer) {
            console.error('El contenedor de artistas no se encontró en el DOM.');
            return;
        }
    
        artistContainer.style.display = 'flex'; // Asegura que los elementos estén en fila
        artistContainer.style.overflow = 'hidden'; // Oculta el contenido que sobresale
        artistContainer.style.whiteSpace = 'nowrap'; // Evita que los elementos se envuelvan
    
        // Duplica los elementos para un efecto continuo
        artistContainer.innerHTML += artistContainer.innerHTML;
    
        let scrollAmount = 0;
    
        function slide() {
            scrollAmount += 1; // Incremento para desplazamiento
            if (scrollAmount >= artistContainer.scrollWidth / 2) {
                scrollAmount = 0; // Reinicia cuando llega al final
            }
            artistContainer.scrollLeft = scrollAmount; // Ajusta la posición del desplazamiento
        }
    
        setInterval(slide, 20); // Ajusta la velocidad del desplazamiento aquí
    }
    